  render() {

    let array = []

    function pupulateArray(numCol, numRow) {
      let styleAlive = {
        backgroundColor: "blue"
      }
      let styleDead = {
        backgroundColor: "white"
      }
      for(let i = 0; i < numCol; i++) {
        let fill =[]
        for(let j = 0; j < numRow; j++) {
          fill.push({
            id:`${i}${j}`,
            state: 0,
            alive: () => {
            return (
              <div className={`div div${i}${j}`} style={styleAlive}/>
            )},
            dead: () => {
              return (
                <div className={`div div${i}${j}`} style={styleDead}/>
              )},
          })
        }
        array.push(fill)
        console.log(array)
    }
    }

    function randomStart() {
      for(let i = 0; i < array.length; i++) {

        for(let j = 0; j < array[i].length; j++) {
          let coin = Math.floor(Math.random() * 2);
          console.log(coin)
          if(coin === 0) {
            array[i][j].state = 0
          }
          if(coin === 1) {
            array[i][j].state = 1
          }
        }
      }
    }

    function playLife() {

    function sum(col,row) {
      let cell = array
      let nTopL = cell[row-1][col-1].state
      let nTop = cell[row-1][col].state
      let nTopR = cell[row-1][col+1].state
      let nLeft = cell[row][col-1].state
      let nRight = cell[row][col+1].state
      let nBotL = cell[row+1][col-1].state
      let nBot = cell[row+1][col].state
      let nBotR = cell[row+1][col+1].state
      const neighbors = [nTopL, nTop, nTopR, nLeft, nRight, nBotL, nBot, nBotR]
      let sum = 0
      for(let i = 0; i < neighbors.length; i++) {
        if(i === undefined){
          i++
        }
        sum = sum + neighbors[i]
      }
      return sum
    }

    for(let i = 0; i < array.length; i++) {

      for(let j = 0; j < array[i][j].length; j++) {
        let neighbors = sum(i,j)
        if(neighbors < 2) {
          array[i][j].state = 0
        }
        if(array[i][j].state === 0 && neighbors === 3) {
          array[i][j].state = 1
        }
        if(neighbors > 3) {
          array[i][j].state = 0
        }
      }
    }

  }

  let grid = (array) => {
    while(this.state.running === !false) {
      let gridDiv = []
      for(let i = 0; i < array.length; i++) {
        let cell = []
        for(let j = 0; j < array[i].length; j++){
          if(array[i][j].state === 1) {
          cell.push(array[i][j].alive())
          }
          if(array[i][j].state === 0) {
          cell.push(array[i][j].dead())
          }else { j++}
        }
        gridDiv.push(cell)
      }
      console.log(gridDiv)
      return gridDiv
    }
  }


