import React from 'react';
import './App.css';



export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      running: false,
    }
  }

  handleClick = () => {
    this.setState({
      running: !this.state.running,
    })
    console.log(this.state.running)
  }






