import React from 'react';
import './App.css';



export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      running: false,
    }
  }

  handleClick = () => {
    this.setState({
      running: !this.state.running,
    })
    console.log(this.state.running)
  }



playLife = () => {

  function sum(col,row) {
    let cell = this.state.grid
    let nTopL = cell[row-1][col-1]
    let nTop = cell[row-1][col]
    let nTopR = cell[row-1][col+1]
    let nLeft = cell[row][col-1]
    let nRight = cell[row][col+1]
    let nBotL = cell[row+1][col-1]
    let nBot = cell[row+1][col]
    let nBotR = cell[row+1][col+1]
    const neighbors = [nTopL, nTop, nTopR, nLeft, nRight, nBotL, nBot, nBotR]
    let sum = 0
    for(let i = 0; i < neighbors.length; i++) {
      sum = sum + neighbors[i]
    }
    return sum
  }

  for(let i = 0; i < this.state.grid.length; i++) {

    for(let j = 0; j < this.state.grid[i][j].length; j++) {
      let neighbors = sum(i,j)
      // let array = []
      if(neighbors < 2) {
        let dead = this.state.grid
        dead[i][j].state = 0
        console.log(dead[i][j].state)
        this.setState({
          grid: dead
        })
      }
      if(this.state.grid[i][j].state === 0 && neighbors === 3) {
        let born = this.state.grid
        born[i][j].state = 1
        this.setState({
          grid: born
        })
      }
      if(neighbors > 3) {
        let dead = this.state.grid
        dead[i][j].state = 0
        this.setState({
          grid: dead
        })
      }
    }
  }

}


//16377

componentDidMount() {
  this.pupulateArray(10,10)
  this.randomStart()
  }

  render() {

    let array = []
    //if alive, stays alive if 2 or 3 
    //if alive, will die if less than 2
    //if alive, will die if 4 or more
    //if dead, will be born with exaclty 3 neibohrs
    pupulateArray = (numCol, numRow) => {
      for(let i = 0; i < numCol; i++) {
        let fill =[]
        for(let j = 0; j < numRow; j++) {
          fill.push({
            id:`${i}${j}`,
            state: 0,
            alive: () => {
            return (
              <div className={`div div${i}${j}`} background-color="blue"/>
            )},
            dead: () => {
              return (
                <div className={`div div${i}${j}`} background-color="white"/>
              )},
          })
        }
        array.push(fill)
    }
  }

  randomStart = () => {
    for(let i = 0; i < array.length; i++) {

      for(let j = 0; j < array[i].length; j++) {
        let coin = Math.floor(Math.random() * 2);
        console.log(coin)
        if(coin === 0) {
          array[i][j].state = 0
        }
        if(coin === 1) {
          array[i][j].state = 1
        }
      }
    }
  }

  let grid = (array) => {
    while(this.state.running === !false) {
      let gridDiv = []
      for(let i = 0; i < array.length; i++) {
        let cell = []
        for(let j = 0; j < array[i].length; j++){
          if(array[i][j].state === 1) {
          cell.push(array[i][j].alive())
          }
          cell.push(array[i][j].dead())
        }
        gridDiv.push(cell)
      }
      console.log(gridDiv)
      return gridDiv
    }
  }

  pupulateArray()
  randomStart()

  return (
    <div className="App">
      <button onClick={this.handleClick}>Click me</button>
      {grid(this.state.grid)}
    </div>
  );
  }
}
