import React from 'react';
import './App.css';



export default class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      gridStart: [],
      gridInitialState: [],
      gridRun: [],
      running: false,
    }
  }

  pupulateArray = (numCol, numRow) => {
    let array = []
    let styleAlive = {
      backgroundColor: "blue"
    }
    let styleDead = {
      backgroundColor: "white"
    }
    for(let i = 0; i < numCol; i++) {
      let fill =[]
      for(let j = 0; j < numRow; j++) {
        fill.push({
          id:`${i}${j}`,
          state: 0,
          alive: () => {
          return (
            <div className={`div div${i}${j}`} style={styleAlive}/>
          )},
          dead: () => {
            return (
              <div className={`div div${i}${j}`} style={styleDead}/>
            )},
        })
      }
      array.push(fill)
      this.setState({
        gridStart: array,
      })
  }
  console.log(this.state.gridStart)
  }

  handleClick = () => {

    this.setState({
      running: !this.state.running,
    })
    this.pupulateArray(10, 10)
  }


  randomStart = () => {
    let { gridStart } = this.state
    let gridInitialState = []
    let styleAlive = {
      backgroundColor: "blue"
    }
    let styleDead = {
      backgroundColor: "white"
    }

    for(let i = 0; i < gridStart.length; i++) {

      for(let j = 0; j < gridStart[i].length; j++) {
        let coin = Math.floor(Math.random() * 2);
        console.log(coin)
        if(coin === 0) {
          gridInitialState.push(
            {
              id:`${i}${j}`,
              state: 0,
              alive: () => {
              return (
                <div className={`div div${i}${j}`} style={styleAlive}/>
              )},
              dead: () => {
                return (
                  <div className={`div div${i}${j}`} style={styleDead}/>
                )},
            }
        )
        }
        if(coin === 1) {
          gridInitialState.push(
          {
            id:`${i}${j}`,
            state: 1,
            alive: () => {
            return (
              <div className={`div div${i}${j}`} style={styleAlive}/>
            )},
            dead: () => {
              return (
                <div className={`div div${i}${j}`} style={styleDead}/>
              )},
          })
        }
      }
    }
    this.setState({
      gridInitialState
    })
    console.log(gridInitialState)
  }
//16377
  playLife = () => {

  let cell = this.state.gridInitialState
  function sum(row,col) {
    console.log(cell)

    let nTopL = row === 0 || col === 0? 0: cell[row-1][col-1].state;
    let nTop = row  === 0? 0: cell[row-1][col].state;
    let nTopR = (row === 0) || (col > cell.length-2)? 0: cell[row-1][col+1].state;
    let nLeft = col === 0? 0: cell[row][col-1].state;
    let nRight = col > cell.length-2? 0: cell[row][col+1].state;
    let nBotL = (row > cell.length-2) || (col === 0)? 0: cell[row+1][col-1].state;
    let nBot = row > cell.length-2? 0: cell[row+1][col].state;
    let nBotR = (row > cell.length-2) || (col > cell.length-2)? 0: cell[row+1][col+1].state;
    const neighbors = [nTopL, nTop, nTopR, nLeft, nRight, nBotL, nBot, nBotR]
    let sum = 0

    neighbors.filter(neigh => {return neigh !== undefined})
    console.log(neighbors)
    for(let i = 0; i < neighbors.length; i++) {
      sum = sum + neighbors[i]
    }
    console.log(sum)
    return sum
  }

for(let i = 0; i < cell.length; i++) {

  for(let j = 0; j < cell[i].length; j++) {
    if(sum(i,j) < 2) {
      cell[i][j].state = 0
    }
    if(cell[i][j].state === 0 && sum(i,j) === 3) {
      cell[i][j].state = 1
    }
    if(sum(i,j) === 2) {
      cell[i][j].state = 1
    }
    if(sum(i,j) > 3) {
      cell[i][j].state = 0
    }
  }
}
console.log(cell)
this.grid(cell)
}

grid = (array) => {
  while(this.state.running === !false) {
    let gridDiv = []
    for(let i = 0; i < array.length; i++) {
      let cell = []
      for(let j = 0; j < array[i].length; j++){
        if(array[i][j].state === 1) {
        cell.push(array[i][j].alive())
        }
        if(array[i][j].state === 0) {
        cell.push(array[i][j].dead())
        } else { j++}
      }
      gridDiv.push(cell)
    }
    console.log("gridStateUpdateRan")
    this.setState({
      gridRun: gridDiv,
    })
  }
  }

  render() {
    // pupulateArray(10,10)
    // randomStart()

    // let visualGrid = this.state.grid
    // visualGrid.map()
    console.log(this.state.gridRun)
  // let displayLife = this.state.gridRun.ma;
  return (
    <div className="App">
      <button onClick={this.handleClick}>Click me</button>
      <button className={"randomStart"} onClick={this.randomStart} > Random Start </button>
      <button onClick={this.playLife}> start </button>
      {this.state.gridRun}
    </div>
  );
  }
}
